import unittest
from selenium import webdriver
from selenium.webdriver.common.keys import Keys

class BrowserTest(unittest.TestCase):

    longJarble = "This Text is 4000 characters long, so the rest of it will be nonsense.  "

    driver:webdriver
    def setUp(self):
        self.driver = webdriver.Chrome('./chromedriver')

    def testDeniesLargeSearch(self):
        driver = self.driver
        driver.get("localhost:8080")
        search_bar = driver.find_element_by_name("searchbar")
        search_bar.clear()
        search_bar.send_keys(self.longJarble)
        search_bar.send_keys(Keys.RETURN)

        assert "no" & "results" in driver.page_source

