    def testHasSearchText(self):
        driver = self.driver
        driver.get("localhost:8080")
        search_bar = driver.find_element_by_name("searchbar")
        search_bar.clear()
        search_bar.send_keys("baseball")
        search_bar.send_keys(Keys.RETURN)

        assert "baseball" in driver.page_source
 
